parameters.text:
q = load &quot;SAQL__Wave_Sample_Opportunities3&quot;;
q = group q by &#39;Billing_Country&#39;;
q = foreach q generate &#39;Billing_Country&#39; as &#39;Billing_Country&#39;, sum(&#39;Amount&#39;) as &#39;Revenue&#39;,
sum(sum(&#39;Amount&#39;)) over ([..] partition by all ) as &#39;Trevenue&#39;,
rank() over ([..] partition by all order by sum(&#39;Amount&#39;)) as &#39;Counter&#39;;
q = group q by &#39;Billing_Country&#39;;
q = foreach q generate &#39;Billing_Country&#39; as &#39;Billing_Country&#39;,power(abs(max(&#39;Revenue&#39;) - (max(&#39;Trevenue&#39;) /max(max(&#39;Counter&#39;)) over
([..] partition by all))),2)/max(max(&#39;Counter&#39;)) over ([..] partition by all) as &#39;sqr_diff_Rev&#39;,max(&#39;Trevenue&#39;)
/max(max(&#39;Counter&#39;)) over ([..] partition by all) as &#39;avg_rev&#39;,max(&#39;Revenue&#39;) as &#39;Cntryrev&#39;;
q = group q by &#39;Billing_Country&#39;;
q = foreach q generate &#39;Billing_Country&#39; as &#39;Billing_Country&#39;, max(&#39;Cntryrev&#39;) as &#39;Cntryrev&#39;,
round(max(&#39;avg_rev&#39;)) as &#39;avg_rev&#39;,
round(max(&#39;avg_rev&#39;) + sqrt(sum(sum(&#39;sqr_diff_Rev&#39;)) over ([..] partition by all))) as
&#39;plus_1_std_dev&#39;,round(max(&#39;avg_rev&#39;) - sqrt(sum(sum(&#39;sqr_diff_Rev&#39;)) over ([..] partition by all))) as &#39;minus_1_std_dev&#39;;
