result = group  q2_CYBADGES by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q2_PYBADGES by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_CYBADGES by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_PYBADGES by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q2_CYTRAILBLAZER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q2_PYTRAILBLAZER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_CYTRAILBLAZER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_PYTRAILBLAZER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q2_CYRANGER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q2_PYRANGER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_CYRANGER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; full, q3_PYRANGER by &#39;{{cell(Cloud_Filter_1.selection,0,&quot;level_1&quot;).asString()}}&#39; ,MasterHierarchy by &#39;Level_1&#39;;
