, ((coalesce(sum(q2_CYTRAILBLAZER.Trailblazers), 0) - coalesce(sum(q2_PYTRAILBLAZER.&#39;Trailblazers&#39;), 0)) / coalesce(sum(q2_PYTRAILBLAZER.&#39;Trailblazers&#39;), 0)) * 100 as &#39;{{cell(Quarter_1.selection,0,&quot;Header_name_rate&quot;).asString()}} Trailblazers Growth&#39;, ((coalesce(sum(q3_CYTRAILBLAZER.&#39;Trailblazers&#39;), 0) - coalesce(sum(q3_PYTRAILBLAZER.&#39;Trailblazers&#39;), 0)) / coalesce(sum(q3_PYTRAILBLAZER.&#39;Trailblazers&#39;), 0)) * 100 as &#39;YTD Trailblazers Growth&#39;,  coalesce(sum(q2_CYRANGER.&#39;Ranger&#39;), 0) as &#39;{{cell(Quarter_1.selection,0,&quot;Header_name_absolute&quot;).asString()}} Ranger Absolute&#39;, coalesce(sum(q3_CYRANGER.&#39;Ranger&#39;), 0) as &#39;YTD Ranger Absolute&#39;, ((coalesce(sum(q2_CYRANGER.&#39;Ranger&#39;), 0) - coalesce(sum(q2_PYRANGER.&#39;Ranger&#39;), 0)) / coalesce(sum(q2_PYRANGER.&#39;Ranger&#39;), 0)) * 100 as &#39;{{cell(Quarter_1.selection,0,&quot;Header_name_rate&quot;).asString()}} Ranger Growth&#39;, ((coalesce(sum(q3_CYRANGER.&#39;Ranger&#39;), 0) - coalesce(sum(q3_PYRANGER.&#39;Ranger&#39;), 0)) / coalesce(sum(q3_PYRANGER.&#39;Ranger&#39;), 0)) * 100 as &#39;YTD Ranger Growth&#39;;
--------------------------------------------------------------------------------------------------------------------------------------FINAL PROJECTION-------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
result = order result by &#39;SortOrder&#39; asc;
